			}
		}
	}
}

PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX p: <http://www.wikidata.org/prop/>
PREFIX v: <http://www.wikidata.org/prop/statement/>
PREFIX rdfs:<http://www.w3.org/2000/01/rdf-schema#> 

SELECT ?src ?dest ?dest_chembl ?dest_label ("Drug" as ?dest_type) ("drug-drug-interaction" as ?interaction) ("wikidata / wikipedia" as ?provenance)
WHERE 
{
	SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql>
	{
		?src wdt:P769 ?dest .
		?dest wdt:P592 ?dest_chembl .
		OPTIONAL  {
			?dest rdfs:label ?dest_label . 
			FILTER(lang(?dest_label) = "en") . 
		}
		FILTER (?src IN (${ID|uri-list}))
	}
}

PREFIX wp: <http://vocabularies.wikipathways.org/wp#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX dcterms: <http://purl.org/dc/terms/> PREFIX dc: <http://purl.org/dc/elements/1.1/>
SELECT DISTINCT (<${ID}> as ?src) (?pathway AS ?dest) ?dest_label ("wikipathways" as ?provenance) ("member-of-pathway" as ?interaction) WHERE { SERVICE <http://sparql.wikipathways.org/> { ?geneProduct a wp:GeneProduct . ?pathway dc:title ?dest_label . ?geneProduct dcterms:isPartOf ?pathway . ?pathway a wp:Pathway . ?geneProduct dc:identifier ?xref .
# Namespace fixing. TODO this might be better done as a custom filter, because this way no IN set allowed FILTER (?xref2 = IRI(REPLACE ("${ID}", "http://purl.uniprot.org/geneid/", "http://identifiers.org/ncbigene/"))) . ?xref wp:bdbEntrezGene ?xref2 . } }
